mapper.update:
UPDATE saving_account
SET current_balance = current_balance + #{amount},
    final_amount    = #{final_amount},
    interest_amount = #{interest_amount}
WHERE account_number = #{account_number}

UPDATE saving_account
SET current_balance = current_balance + #{amount}
WHERE account_number = #{account_number}

mapper.select:
SELECT current_balance
FROM saving_account
WHERE account_number = #{account_number}
