mapper.select:
SELECT *
FROM saving_account
WHERE opener_id = #{opener_id}
  AND pet_id = #{pet_id}

mapper.update:
UPDATE saving_account
SET current_balance = current_balance + #{amount},
    progress_rate   = #{progress_rate}
WHERE account_number = #{account_number}

mapper.select:
SELECT current_balance
FROM saving_account
WHERE account_number = #{account_number}
